package com.extract.cache;

/**
 * 时间常量的毫秒值
 * 
 * @author SHOUSHEN LUAN
 *
 */
public class TimeConstant {
  public static final int ten_SECOND = 10000;
  public static final int fifteen_SECOND = 1000 * 15;
  public static final int thirty_SECOND = 1000 * 30;

  public static final int one_MINUTE = 1000 * 60;
  public static final int ten_MINUTE = 1000 * 60 * 10;
  public static final int fifteen_MINUTE = 1000 * 60 * 15;
  public static final int thirty_MINUTE = 1000 * 60 * 30;
  public static final int one_HOUR = one_MINUTE * 60;
  public static final int twelve_HOUR = one_HOUR * 12;
  public static final int one_DAY = 86400000;
  public static final int seven_DAY = 604800000;
}
